import React, { Component } from 'react';
import store from '../store'
import * as types from '../store/action-types'
import {bindActionCreators} from '../redux'

function increment (){
    return {type:types.INCREMENT2}
}
function decrement (){
    return {type:types.DECREMENT2}
}

// let increments = bindActionCreators(increment, store.dispatch);
// let decrements = bindActionCreators(decrement, store.dispatch);


let actions = bindActionCreators({increment,decrement}, store.dispatch);
export default class Counter2 extends Component {
    state={
        number:store.getState().number
    }
    componentDidMount(){
        this.unsubscribe =  store.subscribe(()=>{
            this.setState({
                number:store.getState().counter2.number
            })
        })
    }
    componentWillUnmount(){
        this.unsubscribe()
    }
    render(){
        return(
            <>
                <p>{this.state.number}</p>
                <button onClick={()=>actions.increment()}>+</button>
                <button onClick={()=>actions.decrement()}>-</button>
            </>
        )
    }
}
