import * as types from '../action-types'

export default function reducer (state={number: 0}, action){
    console.log(action.type,types.INCREMENT)

    switch(action.type){
        case types.INCREMENT:
            return {number: state.number+ 1};
        case types.DECREMENT:
            return {number: state.number- 1}
        default:
            return state
    }
}
