import React from 'react';
import ReactDOM from 'react-dom'

let hookState = [];
let hookIndex = 0;
function useState (initialState) {
  hookState[hookIndex] = hookState[hookIndex] || initialState
  let currentIndex = hookIndex
  function setState (newState) {
    hookState[currentIndex] = newState
    render()
  }
  return [hookState[currentIndex++], setState]
}

function Conser () {
  let [number1, setNumber1] = React.useState(0)
  let [number2, setNumber2] = React.useState(0)

  return(
    <>
      <p>{number1}</p>
      <button onClick={()=>setNumber1(number1+1)}>+</button>
      <p>{number2}</p>
      <button onClick={()=>setNumber2(number2+1)}>+</button>
    </>

  )
}

function render() {
  hookIndex = 0 // 每次渲染的时候需要清空下标
  ReactDOM.render(<Conser />, document.getElementById('root'))
}
