import React from 'react';
import ReactDOM from 'react-dom';

let hookState = [] // 存所有状态的数组
let hookIndex = 0  // 对应的下标
function useState (initialState) {
  hookState[hookIndex] = hookState[hookIndex] || initialState
  let currentIndex = hookIndex  // 利用闭包机制 每次执行setState的时候 都更新对应的值
  function setState (newState) {
    hookState[currentIndex] = newState
    render()
  }
  return [hookState[hookIndex++], setState] // 每次调用的时候 ++  到下一个值
}

function Counter () {
  let [number1, setNumber1] = useState(0)
  let [number2, setNumber2] = useState(0)

  return(
    <>
      <p>{number1}</p>
      <button onClick={()=>{setNumber1(number1+1)}}>+</button>
      <p>{number2}</p>
      <button onClick={()=>{setNumber2(number2+1)}}>+</button>
    </>
  )
}


function render () {
  hookIndex = 0 // 每次重新渲染的时候 都给它重置为0  否则会一直累加
  ReactDOM.render(<Counter />, document.getElementById('root'));
}

