import React from 'react';
import ReactDOM from 'react-dom'

/**
 * useMemo useCallback
 * 1. 优化，有一个依赖项，如果依赖的值变化了就会更新，如果依赖项没有变化就不更新
 */

let Child = ({data, onButtonClick}) =>{
  console.log('child render')
  return(
    <button onClick={()=>onButtonClick()}>{data.number}</button>
  )
}

Child = React.memo(Child);

let App = () =>{
  let [number, setNumber] = React.useState(0)
  let [name, setName] = React.useState('hooks')
  let data = React.useMemo(()=>({number}), [number])
  let addClick = React.useCallback(()=>setNumber(number+1), [number])
  return(
    <>
      <input value={name} onChange={(e)=> setName(e.target.value)}/>
      <Child data={data} onButtonClick={addClick}/>
    </>
  )
}
function render() {
  // hookIndex = 0 // 每次渲染的时候需要清空下标
  ReactDOM.render(<App />, document.getElementById('root'))
}
render()
