import React from 'react';
import ReactDOM from 'react-dom';


function Counter () {
  let [number, setNumber] = React.useState(0)
  return(
    <>
      <p>{number}</p>
      <button onClick={()=>{setNumber(number+1)}}>+</button>
    </>
  )
}

ReactDOM.render(<Counter />, document.getElementById('root'));
